import{j as e}from"./index-CSwXuxyD.js";const i=[{nom:"Chloe, Elizabeth Price",date_lieu:"née le 11 mars 1994 à Arcadia Bay (Oregon), 19 ans",description:`Chloe "la punk aux cheveux bleus" est rebelle, elle n'aime pas l'autorité et a une forte personnalité. Elle n'est pas rancunière et sous ses airs de dure à cuire a un cœur tendre. Elle est quelqu'un avec un franc-parler et qui sait faire des compliments.`,imageClass:"image-chloe"},{nom:"Max(ine) Caufield",date_lieu:"née le 21 septembre 1995 à Arcadia Bay, 18 ans",description:"Max est timide, un peu réservée, discrète, en gros l'opposée de Chloé. Max se soucie des autres et n'aime pas les brutes. Elle est intelligente et très ou trop mature pour son âge. Elle est passionnée de photographie. Au fur et à mesure que l'histoire avance, Max prend plus confiance en elle.",imageClass:"image-max"},{nom:"Rachel, Dawn Amber",date_lieu:"née le 22 juillet 1994 à Long Beach (Californie), 19 ans",description:"Elève talentueuse, Rachel rêve de devenir mannequin, elle est la fille la plus populaire du lycée et tout le monde l'aime sauf Victoria qui est jalouse. Elle est très proche de Chloe, toutes les deux rêvent de quitter Arcadia Bay pour Los Angeles.",imageClass:"image-rachel"},{nom:"Victoria Chase",date_lieu:"née le 14 août 1995 à Seattle (Washington), 18 ans",description:"Riche et arrogante mais avec un côté vulnérable qu'il faut souvent trouver, elle est passionnée de photographie et fan de Mark Jefferson.",imageClass:"image-victoria"},{nom:"Nathan, Joshua Prescott",date_lieu:"né le 29 août 1995 à Fort Lauderdale (Floride), 18 ans",description:"Nathan est un jeune homme riche, perturbé et agressif. Il a des relations tendues avec son père.",imageClass:"image-nathan"},{nom:"Mr Jefferson",date_lieu:"né le 11 avril 1975 à Arcadia Bay, 38 ans",description:"Célèbre photographe qui s'est fait connaître dans les années 90, il est revenu dans sa ville natale pour être professeur de photo.",imageClass:"image-jefferson"},{nom:"Joyce Madsen",date_lieu:"date de naissance inconnue, née à Arcadia Bay",description:"Joyce travaille en tant que serveuse au Two Whales diner d'Arcadia Bay, elle est la mère de la meilleure amie de Max (Chloé) et en conflit permanent avec sa fille qu'elle aime beaucoup.",imageClass:"image-joyce"},{nom:"David Madsen",date_lieu:"né le 18 Mai 1971 à Cullman (Alabama), 42 ans",description:"Il a rencontré Joyce le 27 novembre 2008, il est gardien de la sécurité à l'académie de Blackwell et aussi le beau-père de la meilleure amie de Max.",imageClass:"image-david"},{nom:"Warren Graham",date_lieu:"né le 20 novembre 1996 à Arcadia Bay, 16 ans",description:"C'est le personnage geek du jeu, fan de vieux films sombres et de séries. Il est ami avec Max et amoureux d'elle.",imageClass:"image-warren"},{nom:"Kate Marsh",date_lieu:"née le 12 septembre 1995 à Arcadia Bay, 18 ans",description:"Kate est une chrétienne très croyante. Elle est douce, timide et réservée, elle est amie avec Max.",imageClass:"image-kate"}],t=()=>e.jsxs("div",{className:"character-table",children:[e.jsx("h1",{className:"header-title",children:e.jsx("u",{children:"Bienvenue dans le monde de Life is Strange"})}),e.jsxs("p",{className:"intro-text",children:[`Life is Strange est un jeu d’aventure interactif captivant qui plonge le joueur dans l’histoire de Max Caulfield, une jeune photographe dotée d'un pouvoir singulier : elle peut remonter le temps. Ce don extraordinaire entraîne Max dans une quête pleine de mystères et de dilemmes moraux où chaque décision peut changer le destin d’Arcadia Bay et de ses habitants. Ensemble, vous façonnerez son histoire, explorant des thèmes profonds comme l’amitié, l’amour, le deuil, la responsabilité, le destin et les conséquences de chaque action. Car, après tout, qui ne s'est jamais demandé : "Si je pouvais retourner en arrière, qu'est-ce que je changerais ?"`,e.jsx("br",{}),"Plongez dans l'univers envoûtant de Life is Strange, et découvrez des personnages inoubliables et des histoires uniques qui laissent une empreinte bien au-delà de l’écran."]}),e.jsx("div",{className:"table-container",children:e.jsxs("table",{children:[e.jsx("thead",{children:e.jsxs("tr",{children:[e.jsx("th",{children:"Nom du personnage"}),e.jsx("th",{children:"Date, lieu de naissance et âge pdt l'histoire (Octobre 2013)"}),e.jsx("th",{children:"Description"})]})}),e.jsx("tbody",{children:i.map((a,s)=>e.jsxs("tr",{children:[e.jsxs("td",{children:[e.jsx("b",{children:a.nom}),e.jsx("br",{}),e.jsx("div",{className:a.imageClass})]}),e.jsx("td",{children:a.date_lieu}),e.jsx("td",{children:a.description})]},s))})]})}),e.jsx("p",{children:"J'ai tellement de choses à dire sur ce jeu que je ne sais pas par où commencer..."}),e.jsx("div",{className:"carte"})]});export{t as default};
